import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:8080/api/usuarios/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ username, password }),
      });

      if (response.ok) {
        navigate("/bienvenido");
      } else {
        alert("Credenciales incorrectas");
      }
    } catch (error) {
      console.error("Error al hacer login", error);
    }
  };

  return (
    <div className="min-h-screen bg-blue-50">
      <header className="bg-blue-700 text-white py-6 px-4 text-center">
        <h1 className="text-4xl font-bold">Subestación San Martín</h1>
      </header>

      <main className="max-w-md mx-auto mt-10 p-6">
        <div className="bg-white rounded-lg shadow-lg p-8">
          <h2 className="text-3xl font-bold text-gray-800 mb-6 text-center">
            Iniciar Sesión
          </h2>
          <form onSubmit={handleLogin} className="space-y-6">
            <div>
              <input
                type="text"
                placeholder="Usuario"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
                className="w-full px-4 py-3 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>
            <div>
              <input
                type="password"
                placeholder="Contraseña"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                className="w-full px-4 py-3 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>
            <button
              type="submit"
              className="w-full bg-blue-600 text-white py-3 rounded-lg text-lg font-semibold hover:bg-blue-700 transition-colors"
            >
              Iniciar sesión
            </button>
          </form>
        </div>
      </main>

      <footer className="fixed bottom-0 w-full bg-blue-700 text-white py-4 text-center">
        <p>&copy; 2025 Subestación San Martín - CEDENAR. Todos los derechos reservados.</p>
      </footer>
    </div>
  );
};

export default Login;
